
public class Ex04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = a;
		System.out.println(b);

		b += a;
		System.out.println(b);

		b -= a;
		System.out.println(b);

		b *= a;
		System.out.println(b);

		b /= a;
		System.out.println(b);

		b %= a;
		System.out.println(b);

	}

}
