
public class Ex03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] num = { 1, 2, 3, 4 };
		for (int n : num) {
			System.out.println("정수 배열의 값: " + n);
		}

		String[] name = { "신민아", "한효주", "이보영" };
		for (String n : name) {
			System.out.println("문자열 배열의 값: " + n);
		}

	}

}
