
public class Ex04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] num;
		num = new int[] { 1, 2, 3 };
		for (int n : num) {
			System.out.println("정수 배열 값: " + n);
		}

		String[] name = new String[] { "유승호", "소지섭", "마동석" };
		for (String n : name) {
			System.out.println("문자열 배열 값: " + n);
		}
		
	}

}
